/*
 * idl_atoi - Convert a string of digits into an integer according to base b
 */
static long
idl_atoi(char *s, long b)
{
        long    r = 0;
        long    negative = 0;

        if (*s == '-') {
          negative = 1;
          s++;
        }
        if (b == 8 && *s == '0')
          s++;
        else if (b == 16 && *s == '0' && (*(s + 1) == 'x' || *(s + 1) == 'X'))
          s += 2;

        for (; *s; s++)
          if (*s <= '9' && *s >= '0')
            r = (r * b) + (*s - '0');
          else if (b > 10 && *s <= 'f' && *s >= 'a')
            r = (r * b) + (*s - 'a' + 10);
          else if (b > 10 && *s <= 'F' && *s >= 'A')
            r = (r * b) + (*s - 'A' + 10);
          else
            break;

        if (negative)
          r *= -1;

        return r;
}

